/// <reference types="react" />
declare type OptionProps<T> = {
    /**
     * If the option is currently selected.
     */
    selected: boolean;
    /**
     * The label to display for the option.
     */
    label: string;
    /**
     * The value of the option.
     */
    value: T;
    /**
     * A callback triggered when the option is clicked.
     */
    onClick: (v: T) => void;
    /**
     * If the option is disabled.
     */
    disabled: boolean;
};
/**
 * Custom Option component.
 */
declare function Option<T extends unknown>({ selected, value, label, onClick, disabled, ...props }: OptionProps<T>): JSX.Element;
export default Option;
