/**
 * Time type
 */
export declare type Time = {
    hours: number;
    minutes: number;
};
/**
 * Time display type
 */
export declare type TimeDisplay = {
    hours: number;
    minutes: number;
    meridiem?: Meridiem;
};
export declare type DisplayFormat = '12hr' | '24hr';
export declare enum Meridiem {
    AM = "AM",
    PM = "PM"
}
